	if( !font )
		font = DEFAULTFONT;
	if( pImage->flags & IF_FLAG_FINAL_RENDER )
	{
		bias_x = ( font->bias & 0xF );
		bias_y = ( ( font->bias >> 4 ) & 0xF );
	}
	else
		bias_x = bias_y = 0;
	if( bias_x & 0x8 ) bias_x |= 0xFFFFFFF0;
	if( bias_y & 0x8 ) bias_y |= 0xFFFFFFF0;
	x += bias_x;
	y += bias_y;
	_x = x;
// x;
	if( !pImage || !pc ) return;
