//---------------------------------------------------------------------------
 int  IMGVER(MergeRectangle )( IMAGE_RECTANGLE *r, IMAGE_RECTANGLE *r1, IMAGE_RECTANGLE *r2 )
{
	// results in the union of the area extents...
	// union will always result in a rectangle?...
	int tmp1, tmp2;
	if( r1->x < r2->x )
		r->x = r1->x;
	else
		r->x = r2->x;
	tmp1 = r1->width + r1->x;
	tmp2 = r2->width + r2->x;
	if( tmp1 > tmp2 )
		r->width = tmp1 - r->x;
	else
		r->width = tmp2 - r->x;
	if( r1->y < r2->y )
		r->y = r1->y;
	else
		r->y = r2->y;
	tmp1 = r1->height + r1->y;
	tmp2 = r2->height + r2->y;
	if( tmp1 > tmp2 )
		r->height = tmp1 - r->y;
	else
		r->height = tmp2 - r->y;
	return TRUE;
}
//---------------------------------------------------------------------------
 int  IMGVER(IntersectRectangle) ( IMAGE_RECTANGLE *r, IMAGE_RECTANGLE *r1, IMAGE_RECTANGLE *r2 )
{
	int tmp1, tmp2;
	/*
	lprintf( "Intersecting rectangles........." );
	lprintf( "r1: (%d,%d)-(%d,%d)  (%d,%d)-(%d,%d)"
			  , r1->x, r1->y, r1->width, r1->height
			  , r1->x, r1->y, r1->x + r1->width, r1->y + r1->height );
	lprintf( "r2: (%d,%d)-(%d,%d)  (%d,%d)-(%d,%d)"
			  , r2->x, r2->y, r2->width, r2->height
			  , r2->x, r2->y, r2->x + r2->width, r2->y + r2->height );
	*/
	if( ( r1->x > (r2->x+(int32_t)r2->width) ) ||
		 ( r2->x > (r1->x+(int32_t)r1->width) ) ||
		 ( r1->y > (r2->y+(int32_t)r2->height) ) ||
		( r2->y > (r1->y+(int32_t)r1->height) ) )
	{
		//DebugBreak();
		return FALSE;
	}
	if( r1->x > r2->x )
		r->x = r1->x;
	else
		r->x = r2->x;
	tmp1 = r1->width + r1->x;
	tmp2 = r2->width + r2->x;
	if( tmp1 < tmp2 )
		if( r->x > tmp1 )
			r->width = 0;
		else
			r->width = tmp1 - r->x;
	else
		if( r->x > tmp2 )
			r->width = 0;
		else
			r->width = tmp2 - r->x;
	if( r1->y > r2->y )
		r->y = r1->y;
	else
		r->y = r2->y;
	tmp1 = r1->height + r1->y;
	tmp2 = r2->height + r2->y;
	if( tmp1 < tmp2 )
		if( r->y > tmp1 )
			r->height = 0;
		else
			r->height = tmp1 - r->y;
	else
		if( r->y > tmp2 )
			r->height = 0;
		else
			r->height = tmp2 - r->y;
	if( ( r->width == 0 )
		||( r->height == 0 ) )
	{
		return FALSE;
	}
	/*
	lprintf( "r : (%d,%d)-(%d,%d)  (%d,%d)-(%d,%d)"
			  , r->x, r->y, r->width, r->height
			  , r->x, r->y, r->x + r->width, r->y + r->height );
	*/
	return TRUE;
}
