void  IMGVER(SetColor)( PCDATA po, int oo, int w, int h, CDATA color )
{
	oo /= 4;
	{
		int r;
		r = 0;
		while( r < h )
		{
			int col;
			col = 0;
			while( col < w )
			{
				*(CDATA*)po = color;
				po++;
				col++;
			}
			po += oo;
			r++;
		}
	}
}
void  IMGVER(SetColorAlpha)( PCDATA po, int oo, int w, int h, CDATA color )
{
	int alpha = AlphaVal(color);
	oo /= 4;
	{
		int r;
		r = 0;
		while( r < h )
		{
			int col;
			col = 0;
			while( col < w )
			{
				*po = DOALPHA( *po, color, alpha );
				po++;
				col++;
			}
			po += oo;
			r++;
		}
	}
}
