//--------------------------------------------------------------------------
POINTER  PeekQueueEx	 ( PLINKQUEUE plq, int idx )
{
	size_t top;
	if( !plq )
		return NULL;
	if( idx < 0 )
	{
		idx++;
		for( top = plq->Top?(plq->Top - 1):(plq->Cnt-1)
			 ; idx && top != plq->Bottom
			  ; )
		{
			idx++;
			if( !top ) top = plq->Cnt - 1;
			else top--;
		}
		if( idx == 0 )
		{
			if( plq->Top == plq->Bottom )
				return NULL;
			return plq->pNode[top];
		}
	}
	else
	{
		for( top = plq->Bottom
			 ; idx != -1 && top != plq->Top
			  ; )
		{
			if( idx ) {
				top++;
				if( top >= plq->Cnt )
					top-=plq->Cnt;
				idx--;
			}else { idx = -1; break; }
		}
		if( idx == -1 )
			return plq->pNode[top];
	}
	return NULL;
}
POINTER  PeekQueue ( PLINKQUEUE plq )
{
	return PeekQueueEx( plq, 0 );
}
